    /**
     * 禁用函数检查
     * @param $funName
     * @return bool
     */
    private function disabledFun($funName)
    {
        $disabled = explode(',', ini_get('disable_functions'));
        return in_array($funName, $disabled);
    }

    /**
     * 实例化Minion
     * @return S3Client
     */
    private function minio()
    {
        $s3Client = new S3Client([
            'version' => 'latest',
            'region' => env('MINO_DEFAULT_REGION'),
            'endpoint' => env('MINO_ENDPOINT'),
            'use_path_style_endpoint' => env('MINO_USE_PATH_STYLE_ENDPOINT'),
            'credentials' => [
                'key' => env('MINO_ACCESS_KEY_ID'),
                'secret' => env('MINO_SECRET_ACCESS_KEY')
            ],
        ]);
        return $s3Client;
    }

    /**
     * 文件下载
     * @param $url
     * @param $ts
     * @return array
     */
    private function download($url, $type = '')
    {
        $urlParse = parse_url($url);
        $pathInfo = pathinfo($urlParse['path']);
        if (isset($urlParse['port']))
        {
            $port = $urlParse['port'];
        } else {
            $port = ($urlParse['scheme'] == 'https') ? 443 : 80;
        }
        $host = $urlParse['host'];
        if (!empty($type))
        {
            $type = $type . '/';
        }
        $this->dirCreate($this->basePath . $type);
        $saveFile = $this->basePath . $type . $pathInfo['basename'];
        run(function () use ($host, $port, $urlParse, $saveFile) {
            $client = new Client($host, $port, true);
            $client->set(['timeout' => -1]);
            $client->setHeaders([
                'Host' => $host,
                'User-Agent' => 'Chrome/49.0.2587.3',
                'Accept' => '*',
                'Accept-Encoding' => 'gzip'
            ]);
            $client->download($urlParse['path'], $saveFile);
        });
        return ['saveFile' => $saveFile, 'filename' => $pathInfo['basename'], 'extension' => $pathInfo['extension']];
    }

    /**
     * 单文件上传
     * @param $current
     * @param $downloadInfo
     * @param $lotteryType
     * @return mixed
     */
    private function upload($current, $downloadInfo, $lotteryType)
    {
        switch ($downloadInfo['extension'])
        {
            case 'mp4':
                $contentType = 'video/mp4';
                break;

            case 'jpg':
                $contentType = 'image/jpeg';
                break;
        }
        switch ($lotteryType)
        {
            case '1':
                $type = 'hk';
                break;

            case '2':
                $type = 'am';
                break;

            case '5':
                $type = 'am2';
                break;

            case '6':
                $type = 'kl28';
                break;

            case '7':
                $type = 'oldam';
                break;
        }
        $s3Client = $this->minio();
        $result = $s3Client->putObject([
            'Bucket' => env('MINO_BUCKET'),
            'Key' => 'lottery/' . date('Y-m-d', $current['createTime'] / 1000) . '/'.$type.'/' . $downloadInfo['filename'],
            'SourceFile' => $downloadInfo['saveFile'],
            'ContentType' => $contentType,
        ]);
        $upParse = parse_url($result['ObjectURL']);
        unlink($downloadInfo['saveFile']);
        return $upParse['path'];
    }

    /**
     * 分片上传
     * @param $current
     * @param $downloadInfo
     * @param $lotteryType
     * @return mixed
     */
    private function multipartUpload($current, $downloadInfo, $lotteryType)
    {
        switch ($lotteryType)
        {
            case '1':
                $type = 'hk';
                break;

            case '2':
                $type = 'am';
                break;

            case '5':
                $type = 'am2';
                break;

            case '6':
                $type = 'kl28';
                break;

            case '7':
                $type = 'oldam';
                break;
        }
        $s3Client = $this->minio();
        $keyname = 'lottery/' . date('Y-m-d', $current['createTime'] / 1000) . '/'.$type.'/' . $downloadInfo['filename'];
        $result = $s3Client->createMultipartUpload([
            'Bucket'       => env('MINO_BUCKET'),
            'Key'          => $keyname,
            'StorageClass' => 'REDUCED_REDUNDANCY',
        ]);
        $uploadId = $result['UploadId'];
        try {
            $file = fopen($downloadInfo['saveFile'], 'r');
            $partNumber = 1;
            $parts = [];
            while (!feof($file)) {
                $result = $s3Client->uploadPart([
                    'Bucket'     => env('MINO_BUCKET'),
                    'Key'        => $keyname,
                    'UploadId'   => $uploadId,
                    'PartNumber' => $partNumber,
                    'Body'       => fread($file, 5 * 1024 * 1024),
                ]);
                $parts['Parts'][$partNumber] = [
                    'PartNumber' => $partNumber,
                    'ETag' => $result['ETag'],
                ];
                $partNumber++;
            }
            fclose($file);
            unset($file);
        } catch (S3Exception $e) {
            $s3Client->abortMultipartUpload([
                'Bucket'   => env('MINO_BUCKET'),
                'Key'      => $keyname,
                'UploadId' => $uploadId
            ]);
        }
        $result = $s3Client->completeMultipartUpload([
            'Bucket'   => env('MINO_BUCKET'),
            'Key'      => $keyname,
            'UploadId' => $uploadId,
            'MultipartUpload' => $parts,
        ]);
        $upParse = parse_url($result['Location']);
        unset($parts);
        unset($result);
        unlink($downloadInfo['saveFile']);
        return $upParse['path'];

    }

    /**
     * CURL
     * @param $url
     * @return false|string
     */
    private function curl($url)
    {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_HTTPGET, true);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        $header = ['User-Agent: Chrome/49.0.2587.3'];
        curl_setopt($ch, CURLOPT_HTTPHEADER, $header);
        curl_setopt($ch, CURLOPT_HEADER, 1);
        curl_setopt($ch, CURLINFO_HEADER_OUT, true);
        curl_setopt($ch, CURLOPT_URL, $url);
        $result = curl_exec($ch);
        curl_close($ch);
        preg_match('/Location: (.*)/i', $result, $matches);
        if (isset($matches[1]))
        {
            return trim($matches[1]);
        } else {
            return false;
        }
    }

    /**
     * 命令行执行
     * @param $cmd
     * @return false|string
     */
    private function execCmd($cmd)
    {
        $output = trim(shell_exec("$cmd 2>&1"));
        if ($output !== "")
        {
            return $output;
        }
        return false;
    }

    /**
     * 清理目录
     * @param $dir
     * @return void
     */
    private function delFile($dir)
    {
        $dh = opendir($dir);
        while ($file = readdir($dh))
        {
            if ($file != '.' && $file != '..')
            {
                $fullpath = $dir . '/' . $file;
                if (!is_dir($fullpath))
                {
                    unlink($fullpath);
                }
            }
        }
    }

    /**
     * 创建目录
     * @param $dir
     * @return void
     */
    private function dirCreate($dir)
    {
        if (!is_dir($dir))
        {
            mkdir($dir, 0777, true);
        }
    }

