    private function update($lotteryType)
    {
        if ($lotteryType == 1)
        {
            // 港彩
            if ($this->disabledFun('shell_exec'))
            {
                exit('请开启shell_exec');
            }
            $tsCacheDir = $this->basePath . 'xg/';
            while (true)
            {
                $hour = date('H'); //当前时
                $point = date('i'); //当前分
                $nextDate = Redis::get('lottery_real_open_date_1'); //下期开奖年月日
                if (!$nextDate)
                {
                    echo '下期开奖时间未获取到' . PHP_EOL;
                    sleep(20);
                    continue;
                }

                if ($nextDate == date('Y-m-d') && $hour == 21 && $point == 30)
                {
                    $tempArr = [];
                    $startTime = time();

                    $liveUrl = DB::table('auth_configs')->value('xg_live');
                    if (!$liveUrl)
                    {
                        echo '直播源地址未获取到' . PHP_EOL;
                        sleep(20);
                        continue;
                    }
                    // 进入先清空目录
                    $this->delFile($this->basePath . 'xg');

                    while (true)
                    {
                        try {
                            $response = Http::get($liveUrl);
                            preg_match_all("/media-(.*)\.ts/sU", $response->body(),$matchesTs);
                            if (isset($matchesTs[0]))
                            {
                                foreach ($matchesTs[0] as $item)
                                {
                                    if (!in_array($item, $tempArr))
                                    {
                                        $tempArr[] = $item;
                                        $this->download(substr($liveUrl, 0, strrpos($liveUrl, '/')+1) . $item, 'xg');
                                        file_put_contents($tsCacheDir . 'file.txt', "file '" . $tsCacheDir . $item . "'\n", FILE_APPEND);
                                    }
                                }
                            }
                            if (time() - $startTime > 300)
                            {
                                break;
                            }
                            sleep(2);
                        } catch (\Exception $exception) {
                            echo $exception->getMessage() . PHP_EOL;
                        }
                    }
                    $this->execCmd('/usr/local/ffmpeg/bin/ffmpeg -f concat -safe 0 -i ' . $tsCacheDir . 'file.txt -c copy ' . $tsCacheDir . 'out.mp4');

                    do
                    {
                        $lastNumber = DB::table('history_numbers')->where(['lotteryType' => 1])->orderByDesc('created_at')->first();
                        if ($lastNumber)
                        {
                            $nextIssue = $lastNumber->year . $lastNumber->issue;
                            $video = DB::table('lottery_videos')->where(['lotteryType' => 1, 'issue' => $nextIssue])->first();
                            if (!$video)
                            {
                                file_put_contents($this->basePath . 'xg.jpg', base64_decode($this->xgCover));
                                $downloadPictureInfo = [
                                    'saveFile' => $this->basePath . 'xg.jpg',
                                    'filename' => 'cover.jpg', 'extension' => 'jpg'
                                ];
                                $uploadFilePicturePath = $this->upload(['createTime' => time() * 1000], $downloadPictureInfo, 1);
                                $downloadVideoInfo = [
                                    'saveFile' => $this->basePath . 'xg/out.mp4',
                                    'filename' => $nextIssue . '.mp4', 'extension' => 'mp4'
                                ];
                                $uploadFileVideoPath = $this->multipartUpload(['createTime' => time() * 1000], $downloadVideoInfo, 1);
                                DB::table('lottery_videos')->insert([
                                    'lotteryType' => 1,
                                    'pictureUrl' => $uploadFilePicturePath,
                                    'videoTitle' => $nextIssue . '期开奖视频',
                                    'videoUrl' => $uploadFileVideoPath,
                                    'year' => date('Y'),
                                    'issue' => $nextIssue,
                                    'created_at' => date('Y-m-d H:i:s'),
                                ]);
                                // 最后清空目录
                                $this->delFile($this->basePath . 'xg');
                                break;
                            }
                        }
                        echo "未有新期数" . PHP_EOL;
                        sleep(10);
                    } while(true);
                }
                echo '当前时间：' . $hour . ':' . $point . PHP_EOL;
                sleep(20);
            }
