    public function video49($lotteryType)
    {
        $lastRow = DB::table('lottery_videos')
            ->where('lotteryType', $lotteryType)
            ->orderByDesc('created_at')
            ->first();
        $result = file_get_contents('https://49208c.com/unite49/h5/lottery/video/list?lotteryType=2&year='.date('Y').'&pageSize=30&pageNum=1');
        $data = json_decode($result, true);
        if ($data && $data['code'] == 10000) {
            $sortData = [];
            foreach ($data['data']['recordList'] as $item) {
                if ($lastRow && $item['year'] . $item['period'] == $lastRow->issue) {
                    break;
                }
                if (!$lastRow && $item['year'] == date('Y') - 1) {
                    break;
                }
                $tempArr = [
                    'issue' => $item['year'] . $item['period'],
                    'videoUrl' => $item['videoUrl'],
                    'createTime' => $item['createTime']
                ];
                array_unshift($sortData, $tempArr);
            }

            foreach ($sortData as $value)
            {
                $urlParse = parse_url($value['videoUrl']);
                $pathInfo = pathinfo($urlParse['path']);
                $saveFilePath = $this->basePath . 'xam/' . $pathInfo['basename'];
                file_put_contents($saveFilePath, file_get_contents($value['videoUrl']));
                $downloadVideoInfo = [
                    'saveFile' => $saveFilePath,
                    'filename' => $pathInfo['basename'],
                    'extension' => $pathInfo['extension']
                ];
                $size = filesize($downloadVideoInfo['saveFile']);
                if ($size < 1000)
                {
                    echo $downloadVideoInfo['saveFile'] . PHP_EOL;
                    unlink($downloadVideoInfo['saveFile']);
                    echo "文件大小错误！" . $size . PHP_EOL;
                    break;
                }
                file_put_contents($this->basePath . 'xam.jpg', base64_decode($this->amCover));
                $downloadPictureInfo = [
                    'saveFile' => $this->basePath . 'xam.jpg',
                    'filename' => 'cover.jpg', 'extension' => 'jpg'
                ];
                $uploadFilePicturePath = $this->upload($value, $downloadPictureInfo, $lotteryType);
                $uploadFileVideoPath = $this->multipartUpload($value, $downloadVideoInfo, $lotteryType);
                echo $value['issue'] . '期开奖视频' . PHP_EOL;
                DB::table('lottery_videos')->insert([
                    'lotteryType' => $lotteryType,
                    'pictureUrl' => $uploadFilePicturePath,
                    'videoTitle' => $value['issue'] . '期开奖视频',
                    'videoUrl' => $uploadFileVideoPath,
                    'year' => date('Y'),
                    'issue' => $value['issue'],
                    'created_at' => date('Y-m-d H:i:s', $value['createTime'] / 1000),
                ]);
            }
        }
    }
